mapper.update:
update ABANDONEDANIMAL
set SNUMBER = #{sNumber}, NAME = #{name}, SPECIES = #{species}, KIND = #{kind}, GENDER = #{gender},
    AGE = #{age}, WEIGHT = #{weight}, IMGPATH = #{imgPath}, LOCATION = #{location}, ADMISSIONDATE =
        #{admissionDate},
    UNIQUENESS = #{uniqueness}, DESCRIPTION = #{description}, VIDEO = #{video}, ANIMALSTATE = #{animalState}
where ABNUMBER = #{abNumber}

mapper.select:
select count(*) from ABANDONEDANIMAL
ANIMALSTATE in('보호', '임보')
and SPECIES = #{species}
and GENDER = #{gender}
and ANIMALSTATE = #{animalState}
and ( NAME like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or AGE like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' or UNIQUENESS like '%' || #{keyword} || '%' or DESCRIPTION like '%' || #{keyword} || '%' )

select * from (select ROWNUM as NUM, A.ABNUMBER, A.NAME, A.LOCATION, A.ADMISSIONDATE, A.IMGPATH FROM (select * from ABANDONEDANIMAL
and ANIMALSTATE in('보호', '임보')
and SPECIES = #{species}
and ANIMALSTATE = #{animalState}
and GENDER = #{gender}
and ( NAME like '%' || #{keyword} || '%' or KIND like '%' || #{keyword} || '%' or AGE like '%' || #{keyword} || '%' or LOCATION like '%' || #{keyword} || '%' or UNIQUENESS like '%' || #{keyword} || '%' or DESCRIPTION like '%' || #{keyword} || '%' )
order by ABNUMBER DESC) A) where NUM between #{start} and #{end}

select NAME
from ABANDONEDANIMAL
where ABNUMBER = #{pk}

select SNUMBER
from ABANDONEDANIMAL
where ABNUMBER = #{pk}
