mapper.update:
update BOARD
set TITLE = #{title}, CONTENT = #{content}, CHECKPUBLIC = #{checkPublic}, WRTIME = #{wrTime}
where BNUMBER = #{bNumber}

mapper.select:
select count(*)
from BOARD
where KINDOFBOARD = #{kindOfBoard}

select * from
(select ROWNUM as NUM, A.* from
(select * from BOARD
where KINDOFBOARD = #{kindOfBoard}
order by BNUMBER desc) A)
where NUM between #{start} and #{end}

select B.*, M.ID, M.NAME
from BOARD B, MEMBER M
where B.MNUMBER = M.MNUMBER and BNUMBER = #{bNumber}
