mapper.insert:
insert into LOOKANIMALBOARD(MNUMBER, SPECIES, KIND, LOCATION, TITLE, CONTENT

    ,IMGPATH

)
values (#{mNumber}, #{species}, #{kind}, #{location}, #{title}, #{content}


    ,#{imgPath}

)

mapper.update:
update LOOKANIMALBOARD set SPECIES = #{species}, KIND = #{kind}, LOCATION = #{location},
IMGPATH = #{imgPath},
TITLE = #{title}, CONTENT = #{content} where LANUMBER = #{laNumber}

update LOOKANIMALBOARD
set ANIMALSTATE = '매칭됨'
where LANUMBER = #{laNumber}

update LOOKANIMALBOARD
set ANIMALSTATE = '보호'
where LANUMBER = #{laNumber}

update FINDANIMALBOARD
set ANIMALSTATE = '매칭됨'
where FANUMBER = #{faNumber}

update FINDANIMALBOARD
set ANIMALSTATE = '실종'
where FANUMBER = #{faNumber}
